mapper.select:
SELECT COUNT(*)
FROM cart c
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM cart
WHERE member_id = #{memberId} 
	AND goods_id IN (SELECT item_id FROM item JOIN market USING(market_id) WHERE market_id = #{marketId})

mapper.select:
SELECT o.order_id, order_date, total_price, used_point, card_company, card_number, og.goods_id, og.order_quantity AS goodsList_quantity, it.name AS item_name, it.open_status AS item_open_status, it.market_id, g.price AS item_price, g.shipping_fee, m.name AS market_name, m.open_status AS market_open_status, i.image_src, sm.member_id AS seller_id, sm.name AS seller_name, sm.address AS seller_address, sm.phone AS seller_phone, bm.member_id AS buyer_id, bm.name AS buyer_name, bm.address AS buyer_address, bm.address_detail AS buyer_address_detail, bm.zip AS buyer_zip, bm.phone AS buyer_phone FROM orders o FULL OUTER JOIN order_goods og ON o.order_id = og.order_id FULL OUTER JOIN item it ON og.goods_id = it.item_id FULL OUTER JOIN goods g ON og.goods_id = g.item_id FULL OUTER JOIN market m ON o.seller_id = m.member_id FULL OUTER JOIN image i ON o.seller_id = i.member_id FULL OUTER JOIN member sm ON o.seller_id = sm.member_id FULL OUTER JOIN member bm ON o.buyer_id = bm.member_id
WHERE buyer_id = #{memberId}
WHERE seller_id = #{memberId}

mapper.update:
UPDATE orders
SET tracking_number = #{trackingNumber}
WHERE order_id = #{orderId}

UPDATE orders
SET order_status = #{orderStatus}
WHERE order_id = #{orderId}
